h1{font-size:4.6vw}input[type="text"],input[type="name"],textarea{color:#bfbfbf;background-color:#464b52}textarea[type="text"]{color:#bfbfbf;background-color:#464b52}[data-hover]{position:relative}[data-hover]:after{box-sizing:border-box;position:absolute;color:#fff;max-width:150px;min-width:100px;padding:5px 10px;font-size:12px;content:attr(data-hover);transition:.3s ease-out;opacity:0;text-align:center;border-radius:3px;z-index:9999;pointer-events:none}[data-hover]:hover:after{opacity:1}[data-hover].tt-default:after{background:#212121}[data-hover].tt-caution:after{background:#F9A825}[data-hover].tt-info:after{background:#1565C0}[data-hover].tt-danger:after{background:#C62828}[data-hover].hover-tt-bottom:after{top:50%;right:50%;transform:translate(50%,0%)}[data-hover].hover-tt-bottom:hover:after{top:calc(100% + 10px)}[data-hover].hover-tt-right:after{top:50%;left:50%;transform:translate(-50%,-50%)}[data-hover].hover-tt-right:hover:after{left:calc(100% + 10px);transform:translate(0%,-50%)}[data-hover].hover-tt-left:after{top:50%;right:50%;transform:translate(0%,-50%)}[data-hover].hover-tt-left:hover:after{right:calc(100% + 10px);transform:translate(0%,-50%)}[data-hover].hover-tt-top:after{bottom:50%;right:50%;transform:translate(50%,0%)}[data-hover].hover-tt-top:hover:after{bottom:calc(100% + 10px)}select{-webkit-appearance:none;-moz-appearance:none;appearance:none}.top{position:fixed;left:40px;bottom:25px;z-index:100}